package model.repository;import model.entity.CondicaPredarePrimireDocumenteEntity;import org.springframework.data.jpa.repository.Query;import org.springframework.data.repository.CrudRepository;import java.sql.Date;import java.util.List;public interface CondicaRepository extends CrudRepository<CondicaPredarePrimireDocumenteEntity, Long> {    CondicaPredarePrimireDocumenteEntity findFirstByOrderByIdCondicaPredareSiPrimireDocumenteDesc();    CondicaPredarePrimireDocumenteEntity findOneByNrInregistrareEquals(String nrInregistrare);    @Query(value = "SELECT * FROM condica_predare_primire_documente i WHERE i.rezervat = 1 AND i.data_inregistrare <= ?", nativeQuery = true)    List<CondicaPredarePrimireDocumenteEntity> findAllRezervateDeAnulat(Date dataTinta);}