package model.repository;import model.entity.IesireEntity;import org.springframework.data.jpa.repository.Query;import org.springframework.data.repository.CrudRepository;import java.sql.Date;import java.util.List;public interface IesireRepository extends CrudRepository<IesireEntity, Long> {    @Query(value = "SELECT * FROM iesire i WHERE i.rezervat = 1 AND i.data_iesirii <= ?", nativeQuery = true)    List<IesireEntity> findAllRezervateDeAnulat(Date dataIesirii);}