package model.repository;import model.entity.IntrareEntity;import org.springframework.data.jpa.repository.Query;import org.springframework.data.repository.CrudRepository;import java.sql.Date;import java.util.List;public interface IntrareRepository extends CrudRepository<IntrareEntity, Long> {    IntrareEntity findTop1ByOrderByIdIntrareDesc();    IntrareEntity findOneByNrCrtEquals(String nrCrt);    @Query(value = "SELECT * FROM intrare i WHERE i.rezervat = 1 AND i.data_intrarii <= ?", nativeQuery = true)    List<IntrareEntity> findAllRezervateDeAnulat(Date dataIntrarii);}