package services;import model.entity.DestinatarEntity;import model.repository.DestinatarRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.security.acls.model.AlreadyExistsException;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class DestinatarServiceImpl implements DestinatarService {    @Autowired    private DestinatarRepository destinatarRepository;    @Override    public List<DestinatarEntity> findAllDestinatari() {        return (List<DestinatarEntity>) destinatarRepository.findAll();    }    @Override    public DestinatarEntity save(DestinatarEntity destinatar) {        DestinatarEntity alreadyExists = destinatarRepository.findOneByDestinatarEquals(destinatar.getDestinatar());        if (alreadyExists == null) {            return destinatarRepository.save(destinatar);        } else {            throw new AlreadyExistsException("Destinatarul exist&#259; deja!");        }    }}