package services;import model.entity.*;import model.repository.*;import model.response.JSONResponse;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import java.sql.Timestamp;import java.util.List;@Servicepublic class RegistruServiceImpl implements RegistruService {    @Autowired    private RegistruAOUGRepository registruAOUGRepository;    @Autowired    private RegistruAOUGViewRepository registruAOUGViewRepository;    @Autowired    private IesireRepository iesireRepository;    @Autowired    private IntrareViewRepository intrareViewRepository;    @Autowired    private IntrareRepository intrareRepository;    @Autowired    private RegistruOGBViewRepository registruOGBViewRepository;    @Autowired    private RegistruOGBRepository registruOGBRepository;    @Autowired    private CondicaViewRepository condicaViewRepository;    @Autowired    private CondicaRepository condicaRepository;    @Override    @Transactional    public RegistruAOUGEntity saveAOUG(RegistruAOUGEntity entity) {        entity.setRezervat(0);        entity.setAnulat(0);        entity.setCreatDe(UserUtils.getLoggedInUsername());        entity.setCreatLa(new Timestamp(System.currentTimeMillis()));        return registruAOUGRepository.save(entity);    }    @Override    public RegistruOGBEntity saveOGB(RegistruOGBEntity entity) {        entity.setRezervat(0);        entity.setAnulat(0);        entity.setCreatDe(UserUtils.getLoggedInUsername());        entity.setCreatLa(new Timestamp(System.currentTimeMillis()));        return registruOGBRepository.save(entity);    }    @Override    public CondicaPredarePrimireDocumenteEntity saveCondica(CondicaPredarePrimireDocumenteEntity entity) {        entity.setAnulat(0);        entity.setCreatDe(UserUtils.getLoggedInUsername());        entity.setCreatLa(new Timestamp(System.currentTimeMillis()));        return condicaRepository.save(entity);    }    @Override    public IntrareEntity saveIntrare(IntrareEntity entity) {        entity.setRezervat(0);        entity.setAnulat(0);        entity.setCreatDe(UserUtils.getLoggedInUsername());        entity.setCreatLa(new Timestamp(System.currentTimeMillis()));        return intrareRepository.save(entity);    }    @Override    public IesireEntity saveIesire(IesireEntity entity) {        entity.setRezervat(0);        entity.setAnulat(0);        entity.setCreatDe(UserUtils.getLoggedInUsername());        entity.setCreatLa(new Timestamp(System.currentTimeMillis()));        return iesireRepository.save(entity);    }    @Override    @Transactional    public List<IntrareViewEntity> findAllIntrari() {        return (List<IntrareViewEntity>) intrareViewRepository.findAll();    }    @Override    @Transactional    public List<IesireEntity> findAllIesiri() {        return (List<IesireEntity>) iesireRepository.findAll();    }    @Override    @Transactional    public List<RegistruAOUGViewEntity> findAllAOUG() {        return (List<RegistruAOUGViewEntity>) registruAOUGViewRepository.findAll();    }    @Override    @Transactional    public List<RegistruOGBViewEntity> findAllOGB() {        return (List<RegistruOGBViewEntity>) registruOGBViewRepository.findAll();    }    @Override    public List<CondicaPredarePrimireDocumenteViewEntity> findAllCondica() {        return (List<CondicaPredarePrimireDocumenteViewEntity>) condicaViewRepository.findAll();    }    @Override    public JSONResponse formatFizicOGB(long id) {        RegistruOGBEntity ogbEntity = registruOGBRepository.findOne(id);        ogbEntity.setFormatFizic(1);        registruOGBRepository.save(ogbEntity);        return new JSONResponse("Inregistrarea a fost actualizat&#259;");    }    @Override    public JSONResponse formatFizicAOUG(long id) {        RegistruAOUGEntity aougEntity = registruAOUGRepository.findOne(id);        aougEntity.setFormatFizic(1);        registruAOUGRepository.save(aougEntity);        return new JSONResponse("Inregistrarea a fost actualizat&#259;");    }    @Override    public JSONResponse formatFizicIntrare(long id) {        IntrareEntity intrareEntity = intrareRepository.findOne(id);        intrareEntity.setFormatFizic(1);        intrareRepository.save(intrareEntity);        return new JSONResponse("Inregistrarea a fost actualizat&#259;");    }    @Override    public JSONResponse formatFizicIesire(long id) {        IesireEntity iesireEntity = iesireRepository.findOne(id);        iesireEntity.setFormatFizic(1);        iesireRepository.save(iesireEntity);        return new JSONResponse("Inregistrarea a fost actualizat&#259;");    }}