package services;import model.entity.*;import model.repository.*;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import java.sql.Timestamp;import java.util.List;@Servicepublic class RegistruServiceImpl implements RegistruService {    @Autowired    private RegistruAOUGRepository registruAOUGRepository;    @Autowired    private RegistruAOUGViewRepository registruAOUGViewRepository;    @Autowired    private IesireRepository iesireRepository;    @Autowired    private IntrareViewRepository intrareViewRepository;    @Autowired    private IntrareRepository intrareRepository;    @Autowired    private RegistruOGBViewRepository registruOGBViewRepository;    @Autowired    private RegistruOGBRepository registruOGBRepository;    @Autowired    private CondicaViewRepository condicaViewRepository;    @Autowired    private CondicaRepository condicaRepository;    @Override    @Transactional    public RegistruAOUGEntity saveAOUG(RegistruAOUGEntity entity) {        entity.setRezervat(0);        entity.setAnulat(0);        entity.setCreatDe(UserUtils.getLoggedInUsername());        entity.setCreatLa(new Timestamp(System.currentTimeMillis()));        return registruAOUGRepository.save(entity);    }    @Override    public RegistruOGBEntity saveOGB(RegistruOGBEntity entity) {        entity.setRezervat(0);        entity.setAnulat(0);        entity.setCreatDe(UserUtils.getLoggedInUsername());        entity.setCreatLa(new Timestamp(System.currentTimeMillis()));        return registruOGBRepository.save(entity);    }    @Override    public CondicaPredarePrimireDocumenteEntity saveCondica(CondicaPredarePrimireDocumenteEntity entity) {        entity.setAnulat(0);        entity.setCreatDe(UserUtils.getLoggedInUsername());        entity.setCreatLa(new Timestamp(System.currentTimeMillis()));        return condicaRepository.save(entity);    }    @Override    public IntrareEntity saveIntrare(IntrareEntity entity) {        entity.setRezervat(0);        entity.setAnulat(0);        entity.setCreatDe(UserUtils.getLoggedInUsername());        entity.setCreatLa(new Timestamp(System.currentTimeMillis()));        return intrareRepository.save(entity);    }    @Override    public IesireEntity saveIesire(IesireEntity entity) {        entity.setRezervat(0);        entity.setAnulat(0);        entity.setCreatDe(UserUtils.getLoggedInUsername());        entity.setCreatLa(new Timestamp(System.currentTimeMillis()));        return iesireRepository.save(entity);    }    @Override    @Transactional    public List<IntrareViewEntity> findAllIntrari() {        return (List<IntrareViewEntity>) intrareViewRepository.findAll();    }    @Override    @Transactional    public List<IesireEntity> findAllIesiri() {        return (List<IesireEntity>) iesireRepository.findAll();    }    @Override    @Transactional    public List<RegistruAOUGViewEntity> findAllAOUG() {        return (List<RegistruAOUGViewEntity>) registruAOUGViewRepository.findAll();    }    @Override    @Transactional    public List<RegistruOGBViewEntity> findAllOGB() {        return (List<RegistruOGBViewEntity>) registruOGBViewRepository.findAll();    }    @Override    public List<CondicaPredarePrimireDocumenteViewEntity> findAllCondica() {        return (List<CondicaPredarePrimireDocumenteViewEntity>) condicaViewRepository.findAll();    }}