package services;import model.entity.TipContinutEntity;import model.repository.TipContinutRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.security.acls.model.AlreadyExistsException;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import java.util.List;@Servicepublic class TipContinutServiceImpl implements TipContinutService {    @Autowired    private TipContinutRepository tipContinutRepository;    @Override    @Transactional    public List<TipContinutEntity> findAll() {        return (List<TipContinutEntity>) tipContinutRepository.findAll();    }    @Override    @Transactional    public TipContinutEntity save(TipContinutEntity entity) {        TipContinutEntity alreadyExists = tipContinutRepository.findOneByTipContinutEquals(entity.getTipContinut());        if (alreadyExists == null) {            return tipContinutRepository.save(entity);        } else {            throw new AlreadyExistsException("Tipul de con&#539;inut exist&#259; deja!");        }    }    @Override    public TipContinutEntity getTipContinut(long id) {        return tipContinutRepository.findOne(id);    }    @Override    public void deleteTipContinut(long id) {        tipContinutRepository.delete(id);    }}